import { useState, useEffect } from 'react';
import { Route, Routes, BrowserRouter } from 'react-router-dom';
import Header from './components/header';
import Home from './pages/home';
import Top from './pages/top';
import Login from './pages/user/login';
import Logout from './pages/user/logout';
import Register from './pages/user/register';
import './App.css';
import { UserType, CompanyInfoType } from './utils/types';
import { Company } from '../../backend/src/model/company';

function App() {
	const [user, setUser] = useState<UserType>({
		name: '',
		email: '',
		companyInfoList: [],
	});

	const [companyInfoList, setCompanyInfoList] = useState<CompanyInfoType[]>([]);

	const updateUser = (updatedUser: UserType) => {
		setUser(updatedUser);
	};

	const updateCompanyInfoList = (updateCompanyInfoList: CompanyInfoType[]) => {
		setCompanyInfoList(updateCompanyInfoList);
	};

	useEffect(() => {
		if (user.email !== '') {
			const handleCompanyInfoList = async () => {
				try {
					const response = await fetch(
						'http://localhost:5000/companyInfo/getCompanyInfoList',
						{
							method: 'GET',
							headers: {
								Accept: 'application/json',
								'Content-Type': 'application/json',
							},
							body: JSON.stringify({
								email: user.email,
							}),
						}
					);

					const jsonResponse = await response.json();
					const jsonCompanyInfoList = jsonResponse.CompanyInfoList;
					const formattedList: CompanyInfoType[] = [];
					for (let i = 0; i < jsonCompanyInfoList.length; i++) {
						const companyInfo = {
							email: jsonCompanyInfoList[i].email,
							id: jsonCompanyInfoList[i].id,
							memo: jsonCompanyInfoList[i].memo,
						};
						formattedList.push(companyInfo);
					}
					updateCompanyInfoList(formattedList);
				} catch (err) {
					console.log('企業情報の取得に失敗しました\n' + err);
				}
			};
			handleCompanyInfoList();
		} else {
			alert('てすと');
		}
	}, [user.email]);

	return (
		<BrowserRouter>
			<div className="container">
				<Header />
				<Routes>
					<Route path="/" element={<Top />} />
					<Route
						path="/pages/home"
						element={<Home user={user} companyInfoList={companyInfoList} />}
					/>
					<Route
						path="/pages/user/login"
						element={<Login updateUser={updateUser} />}
					/>
					<Route
						path="/pages/user/logout"
						element={<Logout updateUser={updateUser} />}
					/>
					<Route path="/pages/user/register" element={<Register />} />
					<Route path="*" element={<h1>Page Not Found</h1>} />
				</Routes>
			</div>
		</BrowserRouter>
	);
}

export default App;
