import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import {
	CompanyRegisterProps,
	MyCompanyType,
	CompanyType,
} from '../../utils/types';
import CompanyDetail from '../../components/companyDetail';

/**
 * 検索画面から企業の詳細確認できる画面
 * @param param0 企業id
 * @returns
 */
function CompanyRegister() {
	const navigate = useNavigate();
	//idに合致するid
	const [company, setCompany] = useState<CompanyType>();
	/**
	 *idに合致する企業情報を取得する
	 */
	useEffect(() => {
		document.title = `企業新規登録`;
	});
	/**
	 *バックエンドに企業メモを登録する
	 */
	const handleRegisterMyCompany = async () => {
		try {
			await fetch(`http://localhost:5000/myCompany/registerMyCompany`, {
				method: 'POST',
				headers: {
					Accept: 'application/json',
					'Content-Type': 'application/json',
				},
				body: JSON.stringify({
					email: user.email,
					id: id,
					memo: '',
				}),
			});

			if (company) {
				alert(`${company.name}を登録しました`);
			}
		} catch (err) {
			alert('登録に失敗しました');
		}
	};

	/**
	 * 企業リストにidの企業を加える
	 */
	const handleAdd = () => {
		handleRegisterMyCompany();

		navigate('/pages/company/searchCompany');
	};

	/**
	 * キャンセルボタンを押すと検索画面に戻る
	 */
	const handleReturn = () => {
		navigate('/pages/company/searchCompany');
	};

	const handleNameChange = (event: React.ChangeEvent<HTMLInputElement>) => {
		const { value } = event.target;
		if (company) {
			const updatedCompany: CompanyType = {
				id: company.id,
				name: value,
				author: company.author,
				abstract: company.abstract,
				industries: company.industries,
				locations: company.locations,
			};
			setCompany(updatedCompany);
		}
	};

	const handleAbstractChange = (event: React.ChangeEvent<HTMLInputElement>) => {
		const { value } = event.target;
		if (company) {
			const updatedCompany: CompanyType = {
				id: company.id,
				name: company.name,
				author: company.author,
				abstract: value,
				industries: company.industries,
				locations: company.locations,
			};
			setCompany(updatedCompany);
		}
	};

	const handleIndustriesChange = (
		event: React.ChangeEvent<HTMLInputElement>
	) => {
		const { value } = event.target;
		if (company) {
			const updatedCompany: CompanyType = {
				id: company.id,
				name: company.name,
				author: company.author,
				abstract: company.abstract,
				industries: value.split(','),
				locations: company.locations,
			};
			setCompany(updatedCompany);
		}
	};

	const handleLocationsChange = (
		event: React.ChangeEvent<HTMLInputElement>
	) => {
		const { value } = event.target;
		if (company) {
			const updatedCompany: CompanyType = {
				id: company.id,
				name: company.name,
				author: company.author,
				abstract: company.abstract,
				industries: company.industries,
				locations: value.split(','),
			};
			setCompany(updatedCompany);
		}
	};
	if (typeof company !== 'undefined') {
		return (
			<div>
				<CompanyDetail company={company} />
				<button onClick={() => handleAdd()}>登録</button>
				<button onClick={() => handleReturn()}>戻る</button>
			</div>
		);
	} else {
		return <div></div>;
	}
}

export default CompanyRegister;
