import { useState, useEffect } from 'react';
import jwtDecode from 'jwt-decode';
import { UserType } from './types';


const useToken = (loginFlag: boolean): typeof loginUser => {
    const [loginUser, setLoginUser] = useState<UserType>({
        name: '',
        email: '',
    });
    //const navigate = useNavigate();
    const token = localStorage.getItem('token');
    const handleToken = (loginFlag: boolean) => {
        if (token) {
            const decoded: { email: string, name: string } = jwtDecode(token);

            const user: UserType = {
                name: decoded.name,
                email: decoded.email,
            };
            setLoginUser(user);
        }
    }
    useEffect(() => {
        console.log("useToken");
        handleToken(loginFlag);
    }, [token]);


    return loginUser;
};

export default useToken;
