import { useParams, useNavigate } from 'react-router-dom';
import { useEffect } from 'react';
import { CompanyAddProps, CompanyType } from '../../utils/types';
import CompanyDetail from '../../components/companyDetail';

function CompanyAdd({ searchedCompanyList }: CompanyAddProps) {
	const { email, id } = useParams();
	const navigate = useNavigate();
	// 対応する会社情報を探す
	const company: CompanyType | undefined = searchedCompanyList.find(
		(info) => info.id === id
	);

	const handleRegister = async () => {
		try {
			await fetch(
				`http://localhost:5000/myCompany/updateMyCompany/${email}/${id}`,
				{
					method: 'POST',
					headers: {
						'Content-Type': 'application/json',
					},
					body: JSON.stringify({
						email: email,
						id: id,
						memo: '',
					}),
				}
			);
			if (company) {
				alert(`${company.name}のメモを登録しました`);
			}
		} catch (err) {
			alert('更新に失敗しました');
		}
	};

	/**
	 *完了ボタンが押されたときの処理を実装する
	 */
	const handleComplete = () => {
		handleRegister();
		navigate('/pages/home');
	};

	const handleReturn = () => {
		navigate('/pages/home');
	};

	/**
	 * タイトルを更新する
	 */
	useEffect(() => {
		if (company) {
			document.title = `${company.name}のメモ`;
		}
	});

	return (
		<div>
			{company ? <CompanyDetail company={company} /> : null}

			<button onClick={handleComplete}>追加</button>
			<button onClick={handleReturn}>戻る</button>
		</div>
	);
}

export default CompanyAdd;
