import React, { useState, useEffect } from 'react';
import { CompanyType } from '../../utils/types';

function SearchCompany() {
	const [word, setWord] = useState<string>('');
	const [searchedCompany, setSearchedCompany] = useState<CompanyType[]>([]);
	const updateWord = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
		setWord(event.target.value);
	};
	useEffect(() => {
		document.title = `企業検索`;
	});

	const columns = [
		{
			Header: '企業名',
			accessor: 'name',
		},
		{
			Header: '概要',
			accessor: 'abstract',
		},
		// 他のカラムを追加する場合はここに定義します
	];

	const data = searchedCompany; // 検索結果のデータを使用します

	const handleSearch = async () => {
		try {
			const response = await fetch(
				`http://localhost:5000/company/searchCompany/?words=${word}`,
				{
					method: 'GET',
					headers: {
						'Content-Type': 'application/json',
					},
				}
			);

			const jsonResponse = await response.json();
			setSearchedCompany(jsonResponse.companyList);
			alert(searchedCompany.length);
		} catch (err) {
			alert('更新に失敗しました');
		}
	};

	const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
		useTable({
			columns,
			data,
		});

	return (
		<div>
			<h1>企業検索</h1>
			<div>
				<textarea
					className="search-company__textarea"
					value={word}
					onChange={updateWord}
					placeholder="キーワードを入力してください"
				/>
			</div>
			<button className="search-company__button" onClick={handleSearch}>
				検索
			</button>
			<div>
				<table {...getTableProps()} className="search-company__table">
					<thead>
						{headerGroups.map((headerGroup) => (
							<tr {...headerGroup.getHeaderGroupProps()}>
								{headerGroup.headers.map((column) => (
									<th {...column.getHeaderProps()}>
										{column.render('Header')}
									</th>
								))}
							</tr>
						))}
					</thead>
					<tbody {...getTableBodyProps()}>
						{rows.map((row) => {
							prepareRow(row);
							return (
								<tr {...row.getRowProps()}>
									{row.cells.map((cell) => (
										<td {...cell.getCellProps()}>{cell.render('Cell')}</td>
									))}
								</tr>
							);
						})}
					</tbody>
				</table>
			</div>
		</div>
	);
}

export default SearchCompany;
