import { useState, useEffect } from 'react';
import jwtDecode from 'jwt-decode';
import { UserType } from './types';

const useToken = (): UserType | null => {
    const [loginUser, setLoginUser] = useState<UserType | null>(null); // 初期値をnullに設定

    const handleToken = () => {
        const token = localStorage.getItem('token');
        if (token) {
            const decoded: { email: string; name: string } = jwtDecode(token);

            const user: UserType = {
                name: decoded.name,
                email: decoded.email,
            };
            setLoginUser(user);
        } else {
            setLoginUser({
                name: "",
                email: ""
            }); // トークンがない場合はnullに設定
        }
    };

    useEffect(() => {
        console.log("useToken");
        handleToken();
    }, []);

    return loginUser;
};

export default useToken;
