import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { CompanyDetailProps, CompanyType } from '../../utils/types';
import { Company } from '../../../../backend/src/model/company';

function CompanyDetail({ searchedCompany }: CompanyDetailProps) {
	const { id } = useParams<{ id: string }>();
	const navigate = useNavigate();
	const [company, setCompany] = useState<CompanyType>();
	useEffect(() => {
		const foundCompany = searchedCompany.find((item) => item.id === id);

		if (foundCompany) {
			setCompany(foundCompany);
			if (typeof company !== 'undefined') {
				document.title = `${company.name}`;
			}
		}
	});
	const handleAdd = () => {
		navigate('/pages/company/searchCompany');
	};
	const handleReturn = () => {
		navigate('/pages/company/searchCompany');
	};
	if (typeof company !== 'undefined') {
		return (
			<div>
				<h1>{company.name}</h1>
				<h2>概要</h2>
				<div>{company.abstract}</div>
				<h2>業種</h2>
				<div>{company.industries.join(',')}</div>
				<h2>勤務地</h2>
				<div>{company.locations.join(',')}</div>
				<button onClick={() => handleAdd()}>登録</button>
				<button onClick={() => handleReturn()}>戻る</button>
			</div>
		);
	} else {
		return <div></div>;
	}
}

export default CompanyDetail;
