import { useParams } from 'react-router-dom';
import { CompanyInfoProps } from '../../utils/types';

function CompanyInfoEdit({
	user,
	companyList,
	companyInfoList,
	updateUser,
	updateCompanyInfoList,
}: CompanyInfoProps) {
	const { email, id } = useParams();

	// 対応する会社情報を探す
	const targetCompanyInfo = companyInfoList.find((info) => info.id === id);

	return (
		<div>{targetCompanyInfo ? `${targetCompanyInfo.name}のメモ` : ''}</div>
	);
}

export default CompanyInfoEdit;
